CREATE TABLE IF NOT EXISTS "orders_orderitem" ("id" char(32) NOT NULL PRIMARY KEY, "created_at" datetime NOT NULL, "updated_at" datetime NOT NULL, "quantity" integer unsigned NOT NULL CHECK ("quantity" >= 0), "order_id" char(32) NOT NULL REFERENCES "orders_order" ("id") DEFERRABLE INITIALLY DEFERRED, "product_id" char(32) NOT NULL REFERENCES "products_product" ("id") DEFERRABLE INITIALLY DEFERRED);
CREATE TABLE IF NOT EXISTS "orders_order" ("id" char(32) NOT NULL PRIMARY KEY, "created_at" datetime NOT NULL, "updated_at" datetime NOT NULL, "total_price" decimal NULL, "status" varchar(20) NOT NULL, "pharmacy_id" char(32) NULL REFERENCES "pharmacy_pharmacy" ("id") DEFERRABLE INITIALLY DEFERRED, "user_id" char(32) NULL REFERENCES "accounts_customuser" ("id") DEFERRABLE INITIALLY DEFERRED, "location" varchar(255) NULL, "owner" varchar(255) NULL, "phone" varchar(255) NULL);
