CREATE TABLE IF NOT EXISTS "orders_cart" ("id" char(32) NOT NULL PRIMARY KEY, "created_at" datetime NOT NULL, "updated_at" datetime NOT NULL, "user_id" char(32) NOT NULL UNIQUE REFERENCES "accounts_customuser" ("id") DEFERRABLE INITIALLY DEFERRED);
CREATE TABLE IF NOT EXISTS "products_product" ("id" char(32) NOT NULL PRIMARY KEY, "created_at" datetime NOT NULL, "updated_at" datetime NOT NULL, "name" varchar(255) NOT NULL, "generic_name" varchar(255) NOT NULL, "form" varchar(255) NOT NULL, "strength" varchar(255) NOT NULL, "factory_company" varchar(255) NOT NULL, "short_description" text NULL, "description" text NULL, "price" integer NULL, "code" varchar(255) NULL, "category_id" char(32) NOT NULL REFERENCES "products_category" ("id") DEFERRABLE INITIALLY DEFERRED, "pharmacy_id" char(32) NOT NULL REFERENCES "pharmacy_pharmacy" ("id") DEFERRABLE INITIALLY DEFERRED, "stock" integer NOT NULL);
CREATE TABLE IF NOT EXISTS "products_coupon" ("id" char(32) NOT NULL PRIMARY KEY, "created_at" datetime NOT NULL, "updated_at" datetime NOT NULL, "code" varchar(50) NOT NULL UNIQUE, "discount_amount" decimal NOT NULL, "start_date" datetime NOT NULL, "end_date" datetime NOT NULL, "active" bool NOT NULL, "usage_limit" integer unsigned NULL CHECK ("usage_limit" >= 0), "usage_count" integer unsigned NOT NULL CHECK ("usage_count" >= 0), "minimum_purchase_amount" decimal NULL, "discount_type" varchar(10) NOT NULL);
CREATE TABLE IF NOT EXISTS "products_discount" ("id" char(32) NOT NULL PRIMARY KEY, "created_at" datetime NOT NULL, "updated_at" datetime NOT NULL, "discount_amount" decimal NOT NULL, "start_date" datetime NULL, "end_date" datetime NULL, "active" bool NOT NULL, "product_id" char(32) NOT NULL REFERENCES "products_product" ("id") DEFERRABLE INITIALLY DEFERRED, "discount_type" varchar(10) NOT NULL);
